%splicemachine
drop table if exists rating_data;
create table RATING_DATA (
    user_id bigint primary key,
    item_id bigint
    rating smallint, 
    timestamp time_stamp
)
call syscs_util.import_data('SPLICE', 'ITEM_DATA', null, '/opt/data/ratings.csv', '|', null, null, null, null, 0, '/opt/data/', null, null);
analyze rating_data;

drop table if exists user_data;
create table USER_DATA (
  user_id bigint primary key,
  age integer,
  gender varchar(1),
  occupation varchar(20),
  zip varchar(10)
);
call syscs_util.import_data('SPLICE', 'USER_DATA', null, '/opt/data/user.csv', '|', null, null, null, null, 0, '/opt/data/', null, null);
analyze user_data;
